###【J3-EX_総合演習】データセットSQL

#▼--------------------以下のSQLをコピーして演習の環境を整えましょう--------------------▼


#[ EMPLOYEE_MST（社員マスタテーブル） ]
CREATE TABLE EMPLOYEE_MST(EMPLOYEE_ID CHAR(9) NOT NULL PRIMARY KEY, EMPLOYEE_NM VARCHAR(100), BIRTH_DATE DATE NOT NULL, SEX CHAR(1) NOT NULL);



#[ EMPLOYEE_ENROLLMENT（社員在籍状況テーブル） ]
CREATE TABLE EMPLOYEE_ENROLLMENT(EMPLOYEE_ID CHAR(9) NOT NULL PRIMARY KEY, ENTERING_DATE DATE NOT NULL, RETIREMENT_DATE DATE, RETIRED_FLG INT(1) DEFAULT 0);



#[ EMPLOYEE_CAREER_HST（社員経歴履歴テーブル） ]
CREATE TABLE EMPLOYEE_CAREER_HST(EMPLOYEE_ID CHAR(9) NOT NULL, CAREER_HST_NO INT(3) NOT NULL, START_DATE DATE NOT NULL, FINISH_DATE DATE , DIVISION_CD CHAR(4) NOT NULL, PRIMARY KEY(EMPLOYEE_ID, CAREER_HST_NO));



#[ DIVISION_MST（部署名称テーブル） ]
CREATE TABLE DIVISION_MST(DIVISION_CD  CHAR(4)  NOT NULL PRIMARY KEY, DIVISION_NM  VARCHAR(100), MANAGER_ID CHAR(9) NOT NULL );
